/* global _, d3, moment, fetch, chartData */
/* eslint no-var: "off", prefer-arrow-callback: "off", no-unused-vars: "off" */

function parseTime(string) {
  string = `0${string}`.slice(-8);
  const day = Math.floor(string.slice(0,2) / 24);
  const hours = `0${string.slice(0,2) % 24}`.slice(-2);
  const minutes = string.slice(3,5);
  return moment.tz(`${moment().add(day, 'days').format('YYYY-MM-DD')}T${hours}:${minutes}`, 'YYYY-MM-DDTHH:mm', 'America/New_York').toDate();
}

function padTimeRange(range) {
  return [
    moment(range[0]).startOf('hour'),
    moment(range[1]).add(30, 'minutes')
  ];
}

function geStopsFromStoptimes(stoptimes, stations) {
  /* eslint-disable-next-line unicorn/no-array-reduce */
  return stoptimes.reduce((memo, stoptime) => {
    const station = stations.find(station => station.stop_id === stoptime.stop_id);
    if (stoptime.arrival_time === stoptime.departure_time) {
      memo.push({
        station,
        time: parseTime(stoptime.arrival_time),
        type: 'stop'
      });
    } else {
      memo.push(
        {
          station,
          time: parseTime(stoptime.arrival_time),
          type: 'arrival'
        }, {
          station,
          time: parseTime(stoptime.departure_time),
          type: 'departure'
        }
      );
    }

    return memo;
  }, []);
}

function formatTimeZoneName(name) {
  switch (name) {
    case "America/New_York":
      return "Eastern";
    case "America/Chicago":
      return "Central";
    case "America/Denver":
      return "Mountain";
    case "America/Phoenix":
      return "Arizona";
    case "America/Los_Angeles":
      return "Pacific";
    default: return "?";
  }
}

function shortenStationName(name) {
  return name.replace(/ Station$/i, "").replace(/ Union$/i, "").replace(/ Amtrak$/i, "").replace(/ Moynihan Train Hall at/i, "").replace(/ Transportation Center$/i, "").replace(/ Regional$/i, "");
}

function formatStationName(station) {
  return `${station.stop_id} | ${shortenStationName(station.name)}`;
}

function formatStopTime(stop) {
  let formattedTime = '';

  if (stop.type === 'arrival') {
    formattedTime += 'Arrives ';
  } else if (stop.type === 'departure') {
    formattedTime += 'Departs ';
  }

  formattedTime += moment(stop.time).tz(stop.station.timezone).format('h:mma z');
  return formattedTime;
}

function getPrimaryDirectionId(stations) {
  const directionGroups = _.groupBy(stations, 'direction_id');
  const largestDirectionGroup = _.maxBy(Object.values(directionGroups), group => group.length);
  return largestDirectionGroup[0].direction_id;
}

