function padTimeRange(range) {
  return [
    moment(range[0]).startOf('hour'),
    moment(range[1]).add(30, 'minutes')
  ];
}

function geStopsFromStoptimes(stoptimes, stations, startOfFirstTrip, days) {
  /* eslint-disable-next-line unicorn/no-array-reduce */
  const startCutoff = moment(startOfFirstTrip).add(2, 'days').subtract(30, 'minutes');
  const endCutoff = moment(startOfFirstTrip).add(2 + days, 'days').add(30, 'minutes');
  const stops = stoptimes.reduce((memo, stoptime) => {
    const station = stations.find(station => station.stop_id === stoptime.stop_id);
    const arrival = moment(stoptime.arrival_time_utc);
    const departure = moment(stoptime.departure_time_utc);
    if (station && departure.isAfter(startCutoff) && arrival.isBefore(endCutoff)) {
      if (stoptime.arrival_time_utc === stoptime.departure_time_utc) {
        memo.push({
          station,
          time: new Date(stoptime.arrival_time_utc),
          type: 'stop'
        });
      } else {
        memo.push(
          {
            station,
            time: new Date(stoptime.arrival_time_utc),
            type: 'arrival'
          }, {
            station,
            time: new Date(stoptime.departure_time_utc),
            type: 'departure'
          }
        );
      }
    }

    return memo;
  }, []);
  return stops.length === 0 ? [] : stops;
}

function shortenStationName(name) {
  return name.replace(/ Passenger Terminal$/i, "").replace(/ Station$/i, "").replace(/ Auto Train$/i, "").replace(/ Union$/i, "").replace(/ Amtrak$/i, "").replace(/ Moynihan Train Hall at/i, "").replace(/ Transportation Center$/i, "").replace(/ Regional$/i, "");
}

function formatTimezoneName(timezone) {
  return moment().tz(timezone).zoneAbbr();
}

function formatStopTime(stop) {
  let formattedTime = moment(stop.time).tz(stop.station.timezone).format('h:mma z');

  if (stop.type === 'arrival') {
    formattedTime += ' (arrival)';
  } else if (stop.type === 'departure') {
    formattedTime += ' (departure)';
  }

  return formattedTime;
}

function getPrimaryDirectionId(stations) {
  const directionGroups = _.groupBy(stations, 'direction_id');
  const largestDirectionGroup = _.maxBy(Object.values(directionGroups), group => group.length);
  return largestDirectionGroup[0].direction_id;
}

function getChartTimezone(stations) {
  var counts = {}
  stations.map((station) => station.timezone).forEach(function(timezone) {
    if(counts[timezone] === undefined) {
      counts[timezone] = 0
    }
    counts[timezone] += 1
  })
  const max = Math.max.apply(null, Object.values(counts));
  return Object.keys(counts).find(key => counts[key] === max);
}

function updateDisplay() {
  const showEven = document.querySelector('input#check_even').checked;
  const showOdd = document.querySelector('input#check_odd').checked;
  d3.selectAll('.even').attr('opacity', showEven ? 1 : 0);
  d3.selectAll('.odd').attr('opacity', showOdd ? 1 : 0);
}

